import React, { useEffect } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Thumbs } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/thumbs";
import PhotoSwipeLightbox from "photoswipe/lightbox";
import "photoswipe/style.css";

const ProductDetailSection = () => {
  const [thumbsSwiper, setThumbsSwiper] = React.useState(null);

  useEffect(() => {
    let lightbox = new PhotoSwipeLightbox({
      gallery: "#product-gallery",
      children: "a",
      showHideAnimationType: "zoom",
      pswpModule: () => import("photoswipe"),
    });
    lightbox.init();

    return () => {
      lightbox.destroy();
      lightbox = null;
    };
  }, []);

  const productImages = [...Array(6)].map(
    (_, i) => `/images/products/fashion/product-${i + 1}.jpg`
  );

  return (
    <section className="flat-single-product flat-spacing-3 py-5">
      <div className="container">
        <div className="row">
          {/* Left side - Image Slider */}
          <div className="col-md-6">
            <div className="tf-product-media-wrap d-flex gap-3">
              {/* Thumbnail vertical slider */}
              <div className="product-thumbs-slider" style={{ width: "100px" }}>
                <Swiper
                  onSwiper={setThumbsSwiper}
                  direction="vertical"
                  slidesPerView={4}
                  spaceBetween={10}
                  modules={[Navigation]}
                  navigation
                  className="thumbs-swiper"
                >
                  {productImages.map((img, i) => (
                    <SwiperSlide key={i}>
                      <img
                        src={img}
                        alt={`Thumb ${i + 1}`}
                        className="img-fluid rounded border"
                        style={{ cursor: "pointer" }}
                      />
                    </SwiperSlide>
                  ))}
                </Swiper>
              </div>

              {/* Main product slider */}
              <div className="flat-wrap-media-product flex-grow-1">
                <Swiper
                  spaceBetween={10}
                  thumbs={{ swiper: thumbsSwiper }}
                  modules={[Navigation, Thumbs]}
                  className="main-swiper"
                  id="product-gallery"
                >
                  {productImages.map((img, i) => (
                    <SwiperSlide key={i}>
                      <a href={img} data-pswp-width="1200" data-pswp-height="1600">
                        <div className="zoom-container position-relative overflow-hidden rounded shadow-sm">
                          <img
                            src={img}
                            alt={`Product ${i + 1}`}
                            className="img-fluid w-100 zoom-image"
                          />
                        </div>
                      </a>
                    </SwiperSlide>
                  ))}
                </Swiper>
              </div>
            </div>
          </div>

          {/* Right side - Product Info */}
          <div className="col-md-6">
            <div className="tf-product-info-wrap">
              <h2 className="fw-bold mb-3">Casual Round Neck T-Shirt</h2>

              <div className="d-flex align-items-center mb-3">
                {[...Array(5)].map((_, i) => (
                  <svg
                    key={i}
                    width="16"
                    height="16"
                    fill="#ef9122"
                    className="me-1"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 14 14"
                  >
                    <path d="M14 5.4091L8.913 5.07466L6.99721 0.261719L5.08143 5.07466L0 5.4091L3.89741 8.7184L2.61849 13.7384L6.99721 10.9707L11.376 13.7384L10.097 8.7184L14 5.4091Z" />
                  </svg>
                ))}
                <span className="ms-2 text-muted">(3,671 reviews)</span>
              </div>

              <div className="product-price mb-3">
                <span className="h3 text-primary fw-bold">$14.99</span>{" "}
                <span className="text-decoration-line-through text-muted">
                  $19.33
                </span>{" "}
                <span className="badge bg-danger ms-2">-29%</span>
              </div>

              <p className="text-muted mb-4">
                A soft cotton round-neck T-shirt with breathable fabric, perfect
                for everyday wear. Designed with comfort and durability in mind.
              </p>

              <div className="d-flex gap-3 align-items-center mb-4">
                <button className="btn btn-outline-primary flex-grow-1">
                  Add to Cart
                </button>
                <button className="btn btn-primary flex-grow-1">Buy Now</button>
              </div>

              <small className="text-success fw-semibold">
                <i className="bi bi-eye me-1"></i> 23 people are viewing this
                product right now
              </small>
            </div>
          </div>
        </div>
      </div>

      {/* Styles */}
      <style jsx>{`
        .zoom-container {
          overflow: hidden;
        }
        .zoom-image {
          transition: transform 0.6s ease;
        }
        .zoom-container:hover .zoom-image {
          transform: scale(1.1);
        }
      `}</style>
    </section>
  );
};

export default ProductDetailSection;
