import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination } from "swiper/modules";

import "swiper/css";
import "swiper/css/pagination";

// Artificial jewellery product images
import product13 from "../images/products/jewelry/product-13.jpg";
import product14 from "../images/products/jewelry/product-14.jpg";
import product15 from "../images/products/jewelry/product-15.jpg";
import product16 from "../images/products/jewelry/product-16.jpg";
import product17 from "../images/products/jewelry/product-17.jpg";
import product18 from "../images/products/jewelry/product-18.jpg";
import product19 from "../images/products/jewelry/product-19.jpg";
import product20 from "../images/products/jewelry/product-20.jpg";

const CombinedProductPage = () => {
  // Artificial jewellery products data
  const artificialProducts = [
    {
      name: "18K Gold Friendship Ring",
      oldPrice: "$109.99",
      newPrice: "$89.99",
      img: product13,
      hoverImg: product14,
    },
    {
      name: "14K Gold Plated Eternity Ring",
      oldPrice: "$99.99",
      newPrice: "$79.99",
      img: product15,
      hoverImg: product16,
    },
    {
      name: "Moissanite Wedding Ring Set",
      oldPrice: "$79.99",
      newPrice: "$59.99",
      img: product17,
      hoverImg: product18,
    },
    {
      name: "Silver Promise Rings",
      oldPrice: "$199.99",
      newPrice: "$159.99",
      img: product19,
      hoverImg: product20,
    },
  ];

  return (
    <div>
      {/* ----------------- Artificial Jewellery Swiper Section ----------------- */}
      <section className="flat-spacing p-0 py-5">
        <div className="container">
          <h2 className="fw-bold mb-4 text-center">Artificial Jewellery</h2>
          <Swiper
            modules={[Pagination]}
            spaceBetween={30}
            slidesPerView={4}
            pagination={{ clickable: true }}
            breakpoints={{
              320: { slidesPerView: 2, spaceBetween: 12 },
              576: { slidesPerView: 2, spaceBetween: 20 },
              768: { slidesPerView: 3, spaceBetween: 30 },
              1200: { slidesPerView: 4, spaceBetween: 48 },
            }}
            className="tf-swiper wow fadeInUp"
          >
            {artificialProducts.map((item, index) => (
              <SwiperSlide key={index}>
                <div className="card-product" style={{ position: "relative", overflow: "hidden" }}>
                  <div className="card-product_wrapper position-relative">
                    <a href="/product-detail" className="product-img d-block position-relative">
                      <img src={item.img} alt={item.name} className="img-product w-100" />
                      <img
                        src={item.hoverImg}
                        alt={item.name}
                        className="img-hover w-100 position-absolute top-0 start-0"
                        style={{
                          opacity: 0,
                          transition: "opacity 0.3s ease",
                        }}
                      />
                    </a>

                    {/* Hover icons */}
                    <ul
                      className="product-action_list list-unstyled d-flex justify-content-center"
                      style={{
                        position: "absolute",
                        top: "50%",
                        left: "50%",
                        transform: "translate(-50%, -50%)",
                        gap: "12px",
                        opacity: 0,
                        visibility: "hidden",
                        transition: "all 0.3s ease",
                      }}
                    >
                      <li>
                        <a
                          href="#shoppingCart"
                          className="hover-tooltip tooltip-left box-icon"
                          style={{
                            width: "42px",
                            height: "42px",
                            borderRadius: "50%",
                            background: "#fff",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            boxShadow: "0 3px 8px rgba(0,0,0,0.2)",
                            transition: "transform 0.2s ease",
                          }}
                        >
                          <span className="icon icon-shopping-cart-simple"></span>
                        </a>
                      </li>
                      <li>
                        <a
                          href="javascript:void(0);"
                          className="hover-tooltip tooltip-left box-icon"
                          style={{
                            width: "42px",
                            height: "42px",
                            borderRadius: "50%",
                            background: "#fff",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            boxShadow: "0 3px 8px rgba(0,0,0,0.2)",
                            transition: "transform 0.2s ease",
                          }}
                        >
                          <span className="icon icon-heart"></span>
                        </a>
                      </li>
                      <li>
                        <a
                          href="#compare"
                          className="hover-tooltip tooltip-left box-icon"
                          style={{
                            width: "42px",
                            height: "42px",
                            borderRadius: "50%",
                            background: "#fff",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            boxShadow: "0 3px 8px rgba(0,0,0,0.2)",
                            transition: "transform 0.2s ease",
                          }}
                        >
                          <span className="icon icon-compare"></span>
                        </a>
                      </li>
                      <li>
                        <a
                          href="#quickView"
                          data-bs-toggle="modal"
                          className="hover-tooltip tooltip-left box-icon"
                          style={{
                            width: "42px",
                            height: "42px",
                            borderRadius: "50%",
                            background: "#fff",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            boxShadow: "0 3px 8px rgba(0,0,0,0.2)",
                            transition: "transform 0.2s ease",
                          }}
                        >
                          <span className="icon icon-view"></span>
                        </a>
                      </li>
                    </ul>
                  </div>

                  {/* Product info */}
                  <div className="card-product_info text-center mt-3">
                    <a href="/product-detail" className="name-product h5 link d-block">
                      {item.name}
                    </a>
                    <div className="price-wrap mb-0">
                      <span className="price-old h6 fw-normal text-decoration-line-through">
                        {item.oldPrice}
                      </span>{" "}
                      <span className="price-new h6 text-danger fw-bold">{item.newPrice}</span>
                    </div>
                  </div>

                  {/* Inline hover logic */}
                  <style>
                    {`
                      .card-product_wrapper:hover .img-hover {
                        opacity: 1;
                      }
                      .card-product_wrapper:hover .product-action_list {
                        opacity: 1;
                        visibility: visible;
                      }
                      .product-action_list a:hover {
                        transform: scale(1.1);
                        background: #f4c542;
                      }
                    `}
                  </style>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
      </section>
    </div>
  );
};

export default CombinedProductPage;
