import React, { useState, useRef } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { FreeMode, Navigation, Thumbs } from "swiper/modules";
import ReactImageMagnify from "react-image-magnify";

import "swiper/css";
import "swiper/css/free-mode";
import "swiper/css/navigation";
import "swiper/css/thumbs";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/styles.css";
import "../css/animate.css";
import "../icon/icomoon/style.css";

const ProductDetailGallery = () => {
  const [thumbsSwiper, setThumbsSwiper] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);

  // Example product images (replace with your own)
  const images = Array.from({ length: 11 }, (_, i) =>
    require(`../images/products/fashion/product-${i + 1}.jpg`)
  );

  return (
    <div className="col-md-6">
      <div className="tf-product-media-wrap sticky-top" style={{ top: "109px" }}>
        <div className="product-thumbs-slider d-flex">
          {/* === Thumbnails Swiper (Vertical) === */}
          <div className="thumbs-col" style={{ width: "25%", marginRight: "10px" }}>
            <Swiper
              onSwiper={setThumbsSwiper}
              direction="vertical"
              spaceBetween={10}
              slidesPerView={4.7}
              freeMode={true}
              watchSlidesProgress={true}
              modules={[FreeMode, Navigation, Thumbs]}
              className="tf-product-media-thumbs"
              style={{ height: "620px" }}
            >
              {images.map((img, i) => (
                <SwiperSlide key={i}>
                  <div className="item">
                    <img
                      src={img}
                      alt={`product-${i}`}
                      className={`thumb-img ${selectedImage === img ? "active" : ""}`}
                      onClick={() => setSelectedImage(img)}
                      style={{
                        width: "100%",
                        height: "150px",
                        objectFit: "cover",
                        borderRadius: "8px",
                        border:
                          selectedImage === img
                            ? "2px solid #ef9122"
                            : "1px solid #ddd",
                        cursor: "pointer",
                      }}
                    />
                  </div>
                </SwiperSlide>
              ))}
            </Swiper>
          </div>

          {/* === Main Image Swiper with Zoom === */}
          <div className="main-col flex-grow-1">
            <Swiper
              spaceBetween={10}
              navigation={true}
              thumbs={{ swiper: thumbsSwiper }}
              modules={[FreeMode, Navigation, Thumbs]}
              className="tf-product-media-main"
              id="gallery-swiper-started"
            >
              {images.map((img, i) => (
                <SwiperSlide key={i}>
                  <div className="item" style={{ textAlign: "center" }}>
                    <ReactImageMagnify
                      {...{
                        smallImage: {
                          alt: `Product-${i}`,
                          isFluidWidth: true,
                          src: img,
                        },
                        largeImage: {
                          src: img,
                          width: 1200,
                          height: 1200,
                        },
                        lensStyle: { backgroundColor: "rgba(0,0,0,.2)" },
                        enlargedImageContainerDimensions: {
                          width: "200%",
                          height: "100%",
                        },
                      }}
                    />
                  </div>
                </SwiperSlide>
              ))}
            </Swiper>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetailGallery;
