import React, { useEffect, useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Thumbs } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/thumbs";
import PhotoSwipeLightbox from "photoswipe/lightbox";
import "photoswipe/style.css";

const ProductDetailSection = () => {
  const [thumbsSwiper, setThumbsSwiper] = useState(null);
  const images = [...Array(6)].map(
    (_, i) => `/images/products/fashion/product-${i + 1}.jpg`
  );

  // Initialize PhotoSwipe Lightbox
  useEffect(() => {
    let lightbox = new PhotoSwipeLightbox({
      gallery: "#gallery-swiper-started",
      children: "a",
      showHideAnimationType: "zoom",
      pswpModule: () => import("photoswipe"),
    });
    lightbox.init();

    return () => {
      lightbox.destroy();
      lightbox = null;
    };
  }, []);

  return (
    <section className="flat-single-product flat-spacing-3">
      <div className="tf-main-product section-image-zoom">
        <div className="container">
          <div className="row">
            {/* ✅ Product Images with Vertical Slider + Hover Zoom */}
            <div className="col-md-6">
              <div className="tf-product-media-wrap sticky-top d-flex gap-3">
                {/* Thumbnails (Vertical Slider) */}
                <div className="product-thumbs-slider" style={{ width: "90px" }}>
                  <Swiper
                    onSwiper={setThumbsSwiper}
                    direction="vertical"
                    slidesPerView={4}
                    spaceBetween={10}
                    modules={[Navigation]}
                    navigation
                    className="tf-product-media-thumbs"
                    style={{ height: "550px" }}
                  >
                    {images.map((img, i) => (
                      <SwiperSlide key={i}>
                        <div className="item">
                          <img
                            src={img}
                            alt={`Thumb ${i + 1}`}
                            className="img-fluid border rounded thumb-image"
                            style={{
                              cursor: "pointer",
                              width: "100%",
                              height: "auto",
                              objectFit: "cover",
                            }}
                          />
                        </div>
                      </SwiperSlide>
                    ))}
                  </Swiper>
                </div>

                {/* Main Gallery with Zoom */}
                <div className="flat-wrap-media-product flex-grow-1">
                  <Swiper
                    spaceBetween={10}
                    thumbs={{ swiper: thumbsSwiper }}
                    modules={[Navigation, Thumbs]}
                    className="tf-product-media-main"
                    id="gallery-swiper-started"
                  >
                    {images.map((img, i) => (
                      <SwiperSlide key={i}>
                        <a
                          href={img}
                          data-pswp-width="1200"
                          data-pswp-height="1600"
                          className="item"
                        >
                          <div className="zoom-container position-relative overflow-hidden rounded shadow-sm">
                            <img
                              src={img}
                              alt={`Product ${i + 1}`}
                              className="zoom-image img-fluid w-100"
                            />
                          </div>
                        </a>
                      </SwiperSlide>
                    ))}
                  </Swiper>
                </div>
              </div>

              {/* ✨ Hover Zoom Effect CSS */}
              <style jsx>{`
                .zoom-container {
                  position: relative;
                  overflow: hidden;
                  border-radius: 12px;
                  background: #fff;
                }

                .zoom-image {
                  transition: transform 0.7s ease, filter 0.3s ease;
                  cursor: zoom-in;
                  transform-origin: center center;
                }

                .zoom-container:hover .zoom-image {
                  transform: scale(1.25);
                  filter: brightness(1.05);
                }

                .thumb-image {
                  opacity: 0.8;
                  transition: all 0.3s ease;
                }

                .thumb-image:hover {
                  opacity: 1;
                  transform: scale(1.05);
                  border: 2px solid #ef9122;
                }

                .swiper-button-prev,
                .swiper-button-next {
                  color: #000;
                }
              `}</style>
            </div>

            {/* ✅ Product Info Section */}
            <div className="col-md-6">
              <div className="tf-product-info-wrap position-relative">
                <div className="tf-product-info-list">
                  <h2 className="product-info-name">Casual Round Neck T-Shirt</h2>

                  <div className="product-info-meta">
                    <div className="rating d-flex gap-3">
                      {[...Array(5)].map((_, i) => (
                        <svg
                          key={i}
                          width="14"
                          height="14"
                          viewBox="0 0 14 14"
                          fill="none"
                          xmlns="http://www.w3.org/2000/svg"
                        >
                          <path
                            d="M14 5.4091L8.913 5.07466L6.99721 0.261719L5.08143 5.07466L0 5.4091L3.89741 8.7184L2.61849 13.7384L6.99721 10.9707L11.376 13.7384L10.097 8.7184L14 5.4091Z"
                            fill="#EF9122"
                          />
                        </svg>
                      ))}
                      <div className="reviews text-main">(3,671 reviews)</div>
                    </div>
                  </div>

                  <div className="tf-product-heading">
                    <div className="product-info-price price-wrap">
                      <span className="price-new h2 fw-bold">$14.99</span>
                      <span className="price-old h6 text-muted">$19.33</span>
                      <p className="badges-on-sale h6 fw-semibold text-danger">
                        <span className="number-sale">-29%</span>
                      </p>
                    </div>
                  </div>

                  <p className="text-muted mt-3">
                    This round-neck cotton t-shirt offers comfort and style for
                    everyday wear. Perfect for casual outings or layering under
                    jackets.
                  </p>

                  <div className="mt-4 d-flex gap-3">
                    <button className="btn btn-dark">
                      <i className="icon icon-shopping-cart-simple"></i> Add to Cart
                    </button>
                    <button className="btn btn-outline-secondary">
                      <i className="icon icon-heart"></i> Wishlist
                    </button>
                  </div>
                </div>
              </div>
            </div>
            {/* /Product Info */}
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProductDetailSection;
