import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Mousewheel } from "swiper/modules";
import ReactImageMagnify from "react-image-magnify";
import "swiper/css";
import "swiper/css/navigation";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/styles.css";
import "../css/animate.css";
import "../icon/icomoon/style.css";

const ProductDetailSection = () => {
  const images = Array.from({ length: 5 }, (_, i) =>
    require(`../images/products/fashion/product-${i + 1}.jpg`)
  );
  const [selectedImage, setSelectedImage] = useState(images[0]);

  return (
    <section className="flat-single-product flat-spacing-3 py-5">
      <div className="tf-main-product section-image-zoom">
        <div className="container">
          <div className="row">
            {/* Left Side: Image Gallery */}
            <div className="col-lg-6 col-md-12 d-flex gap-3">
              {/* Thumbnail Swiper */}
              <div className="thumb-gallery position-relative" style={{ width: "100px" }}>
                <Swiper
                  direction="vertical"
                  slidesPerView={4}
                  spaceBetween={12}
                  mousewheel={true}
                  navigation={true}
                  modules={[Navigation, Mousewheel]}
                  className="product-thumb-swiper"
                  style={{ height: "480px" }}
                >
                  {images.map((img, i) => (
                    <SwiperSlide key={i}>
                      <div
                        className={`thumb-item ${
                          selectedImage === img ? "active-thumb" : ""
                        }`}
                      >
                        <img
                          src={img}
                          alt={`thumb-${i}`}
                          onClick={() => setSelectedImage(img)}
                          className="img-fluid"
                          style={{
                            borderRadius: "8px",
                            cursor: "pointer",
                            border:
                              selectedImage === img
                                ? "2px solid var(--bs-primary)"
                                : "1px solid #e5e5e5",
                            transition: "all 0.3s ease",
                          }}
                        />
                      </div>
                    </SwiperSlide>
                  ))}
                </Swiper>
              </div>

              {/* Main Image with Zoom */}
              <div className="main-image flex-grow-1 d-flex justify-content-center align-items-center">
                <div
                  className="main-img-wrapper border rounded-3 shadow-sm overflow-hidden"
                  style={{ width: "100%", maxWidth: "500px" }}
                >
                  <ReactImageMagnify
                    {...{
                      smallImage: {
                        alt: "Main Product Image",
                        isFluidWidth: true,
                        src: selectedImage,
                      },
                      largeImage: {
                        src: selectedImage,
                        width: 1400,
                        height: 1400,
                      },
                      enlargedImagePosition: "over", // zoom window over the same image
                      lensStyle: { backgroundColor: "rgba(0,0,0,0.2)" },
                      isHintEnabled: true,
                      shouldHideHintAfterFirstActivation: false,
                      imageClassName: "zoom-smooth",
                    }}
                  />
                </div>
              </div>
            </div>

            {/* Right Side: Product Info */}
            <div className="col-lg-6 col-md-12 mt-4 mt-lg-0">
              <div className="tf-product-info-wrap">
                <h2 className="product-info-name mb-3">
                  Classic Cotton Casual T-Shirt
                </h2>

                <div className="rating d-flex gap-2 align-items-center mb-3">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <svg
                      key={i}
                      width="16"
                      height="16"
                      viewBox="0 0 14 14"
                      fill="none"
                      xmlns="http://www.w3.org/2000/svg"
                    >
                      <path
                        d="M14 5.4091L8.913 5.07466L6.99721 0.261719L5.08143 5.07466L0 5.4091L3.89741 8.7184L2.61849 13.7384L6.99721 10.9707L11.376 13.7384L10.097 8.7184L14 5.4091Z"
                        fill="#EF9122"
                      />
                    </svg>
                  ))}
                  <span className="text-muted ms-2">(4.8/5 â€” 2,431 reviews)</span>
                </div>

                <div className="product-info-price mb-3">
                  <span className="price-new h4 fw-bold text-primary">$24.90</span>
                  <span className="price-old text-muted ms-3">
                    <del>$35.00</del>
                  </span>
                  <span className="badge bg-warning ms-2">-29%</span>
                </div>

                <p className="text-muted mb-4">
                  Soft and breathable 100% cotton T-shirt perfect for casual wear.
                  Designed with comfort and durability in mind.
                </p>

                <button className="btn btn-primary px-4 py-2">Add to Cart</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProductDetailSection;
