import React, { useState } from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Mousewheel } from "swiper/modules";
import ReactImageMagnify from "react-image-magnify";
import "swiper/css";
import "swiper/css/navigation";
import "bootstrap/dist/css/bootstrap.min.css";
import "../css/styles.css";
import "../css/animate.css";
import "../icon/icomoon/style.css";
import ProductInfo from "./ProductInfo";

// Main Component
const ProductDetailSection = () => {
  // Replace with your actual product images
  const images = Array.from({ length: 11 }, (_, i) =>
    require(`../images/products/fashion/product-${i + 1}.jpg`)
  );

  const [selectedImage, setSelectedImage] = useState(images[0]);

  return (
    <section className="flat-single-product flat-spacing-3">
      <div className="tf-main-product section-image-zoom">
        <div className="container">
          <div className="row">
            {/* Product Images */}
            <div className="col-md-6 d-flex">
              {/* Vertical Thumbnail Swiper */}
              <div className="col-3 pe-2">
                <Swiper
                  direction="vertical"
                  slidesPerView={4}
                  spaceBetween={10}
                  mousewheel={true}
                  navigation={true}
                  modules={[Navigation, Mousewheel]}
                  className="product-thumb-swiper"
                  style={{ height: "450px" }}
                >
                  {images.map((img, i) => (
                    <SwiperSlide key={i}>
                      <img
                        src={img}
                        alt={`product-${i}`}
                        onClick={() => setSelectedImage(img)}
                        className={`thumb-img ${selectedImage === img ? "active" : ""}`}
                        style={{
                          width: "100%",
                          height: "90px",
                          objectFit: "cover",
                          borderRadius: "8px",
                          border: selectedImage === img ? "2px solid #ef9122" : "1px solid #ddd",
                          cursor: "pointer",
                        }}
                      />
                    </SwiperSlide>
                  ))}
                </Swiper>
              </div>

              {/* Main Product Image with Zoom */}
              <div className="col-9 d-flex justify-content-center align-items-center">
                <div className="main-img-wrapper" style={{ width: "100%", overflow: "hidden" }}>
                  <ReactImageMagnify
                    {...{
                      smallImage: {
                        alt: "Product Image",
                        isFluidWidth: true,
                        src: selectedImage,
                      },
                      largeImage: {
                        src: selectedImage,
                        width: 1200,
                        height: 1200,
                      },
                      lensStyle: { backgroundColor: "rgba(0,0,0,.2)" },
                      enlargedImageContainerDimensions: { width: "200%", height: "100%" },
                    }}
                  />
                </div>
              </div>
            </div>

            {/* Product Info */}
      
    <ProductInfo />
          </div>
        </div>
      </div>
    </section>
  );
};

export default ProductDetailSection;
