import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Pagination } from "swiper/modules";

import "swiper/css";
import "swiper/css/pagination";

// Artificial jewellery product images
import product13 from "../images/products/jewelry/product-13.jpg";
import product14 from "../images/products/jewelry/product-14.jpg";
import product15 from "../images/products/jewelry/product-15.jpg";
import product16 from "../images/products/jewelry/product-16.jpg";
import product17 from "../images/products/jewelry/product-17.jpg";
import product18 from "../images/products/jewelry/product-18.jpg";
import product19 from "../images/products/jewelry/product-19.jpg";
import product20 from "../images/products/jewelry/product-20.jpg";

const CombinedProductPage = () => {
  // Countdown cards data
  const countdownProducts = [
    {
      id: 1,
      link: "gold-ring-detail.html",
      img1: "images/products/product-41.jpg",
      img2: "images/products/product-42.jpg",
      title: "Elegant 22K Gold Ring",
      timer: "25472",
      badge: "Hot",
    },
    {
      id: 2,
      link: "gold-necklace-detail.html",
      img1: "images/products/product-39.jpg",
      img2: "images/products/product-40.jpg",
      title: "Royal Gold Necklace Set",
      timer: "36000",
      badge: "New",
    },
    {
      id: 3,
      link: "gold-bangle-detail.html",
      img1: "images/products/product-37.jpg",
      img2: "images/products/product-38.jpg",
      title: "Traditional Gold Bangles",
      timer: "48000",
      badge: "Limited",
    },
  ];

  // Marquee cards data
  const marqueProducts = [
    {
      id: 1,
      link: "gold-ring-detail.html",
      img1: "images/products/product-37.jpg",
      img2: "images/products/product-38.jpg",
      name: "Elegant 22K Gold Ring",
      offerText1: "âœ¨ PURE 22K GOLD SALE - UP TO 25% OFF âœ¨",
      offerText2: "ðŸ”¥ LIMITED EDITION DESIGNS AVAILABLE ðŸ”¥",
    },
    {
      id: 2,
      link: "gold-necklace-detail.html",
      img1: "images/products/product-39.jpg",
      img2: "images/products/product-40.jpg",
      name: "Royal Gold Necklace Set",
      offerText1: "ðŸ’Ž EXCLUSIVE BRIDAL COLLECTION ðŸ’Ž",
      offerText2: "ðŸŒ¸ FREE CLEANING & POLISH SERVICE ðŸŒ¸",
    },
    {
      id: 3,
      link: "gold-bangle-detail.html",
      img1: "images/products/product-41.jpg",
      img2: "images/products/product-42.jpg",
      name: "Traditional 22K Gold Bangles",
      offerText1: "âœ¨ FESTIVE DEALS - SAVE BIG âœ¨",
      offerText2: "ðŸ’« LIMITED STOCK AVAILABLE ðŸ’«",
    },
  ];

  // Artificial jewellery products data
  const artificialProducts = [
    {
      name: "18K Gold Friendship Ring",
      oldPrice: "$109.99",
      newPrice: "$89.99",
      img: product13,
      hoverImg: product14,
    },
    {
      name: "14K Gold Plated Eternity Ring",
      oldPrice: "$99.99",
      newPrice: "$79.99",
      img: product15,
      hoverImg: product16,
    },
    {
      name: "Moissanite Wedding Ring Set",
      oldPrice: "$79.99",
      newPrice: "$59.99",
      img: product17,
      hoverImg: product18,
    },
    {
      name: "Silver Promise Rings",
      oldPrice: "$199.99",
      newPrice: "$159.99",
      img: product19,
      hoverImg: product20,
    },
  ];

  // Reusable hover icon list
  const HoverIcons = () => (
    <ul
      className="product-action_list list-unstyled d-flex justify-content-center"
      style={{
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
        gap: "12px",
        opacity: 0,
        visibility: "hidden",
        transition: "all 0.3s ease",
        zIndex: 10,
      }}
    >
      {[
        { icon: "icon-shopping-cart-simple", label: "Add to Cart" },
        { icon: "icon-heart", label: "Wishlist" },
        { icon: "icon-compare", label: "Compare" },
        { icon: "icon-view", label: "Quick View" },
      ].map((item, i) => (
        <li key={i}>
          <a
            href="#"
            className="hover-tooltip tooltip-left box-icon"
            style={{
              width: "42px",
              height: "42px",
              borderRadius: "50%",
              background: "#fff",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              boxShadow: "0 3px 8px rgba(0,0,0,0.2)",
              transition: "transform 0.2s ease",
            }}
          >
            <span className={`icon ${item.icon}`}></span>
          </a>
        </li>
      ))}
    </ul>
  );

  return (
    <div>
      {/* ----------------- Countdown Deals Section ----------------- */}
      <section className="countdown-section py-5">
        <div className="container">
          <h2 className="fw-bold mb-4 text-center">Countdown Deals</h2>
          <div className="row">
            {countdownProducts.map((product) => (
              <div className="col-lg-4 col-md-6 mb-4" key={product.id}>
                <div
                  className="card-product_wrapper position-relative overflow-hidden"
                  style={{ transition: "all 0.3s ease" }}
                >
                  <a href={product.link} className="product-img d-block position-relative">
                    <img
                      className="img-product w-100"
                      src={product.img1}
                      alt={product.title}
                    />
                    <img
                      className="img-hover w-100 position-absolute top-0 start-0"
                      src={product.img2}
                      alt={product.title}
                      style={{
                        opacity: 0,
                        transition: "opacity 0.3s ease",
                      }}
                    />
                  </a>

                  {/* Hover icons */}
                  <HoverIcons />

                  {/* Badge */}
                  <ul className="product-badge_list position-absolute top-0 start-0 m-2">
                    <li
                      className="product-badge_item px-3 py-1 rounded text-white"
                      style={{
                        backgroundColor:
                          product.badge === "Limited"
                            ? "#FF5733"
                            : product.badge === "New"
                            ? "#0dcaf0"
                            : "#FFD700",
                      }}
                    >
                      {product.badge}
                    </li>
                  </ul>

                  {/* Title */}
                  <h5 className="text-center mt-3 fw-bold">{product.title}</h5>

                  {/* Hover logic */}
                  <style>
                    {`
                      .card-product_wrapper:hover .img-hover {
                        opacity: 1;
                      }
                      .card-product_wrapper:hover .product-action_list {
                        opacity: 1;
                        visibility: visible;
                      }
                      .product-action_list a:hover {
                        transform: scale(1.1);
                        background: #f4c542;
                      }
                    `}
                  </style>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* ----------------- Special Offers Section ----------------- */}
      <section className="marquee-section py-5 bg-light">
        <div className="container">
          <h2 className="fw-bold mb-4 text-center">Special Offers</h2>
          <div className="row">
            {marqueProducts.map((product) => (
              <div className="col-lg-4 col-md-6 mb-4" key={product.id}>
                <div
                  className="card-product_wrapper position-relative overflow-hidden"
                  style={{ transition: "all 0.3s ease" }}
                >
                  <a href={product.link} className="product-img d-block position-relative">
                    <img
                      className="img-product w-100"
                      src={product.img1}
                      alt={product.name}
                    />
                    <img
                      className="img-hover w-100 position-absolute top-0 start-0"
                      src={product.img2}
                      alt={product.name}
                      style={{
                        opacity: 0,
                        transition: "opacity 0.3s ease",
                      }}
                    />
                  </a>

                  {/* Hover icons */}
                  <HoverIcons />

                  {/* Marquee text */}
                  <div className="product-marquee_sale mt-2">
                    <p className="text-center text-muted mb-0">{product.offerText1}</p>
                    <p className="text-center text-muted mb-0">{product.offerText2}</p>
                  </div>

                  <h4 className="text-center mt-3 fw-bold">{product.name}</h4>

                  <style>
                    {`
                      .card-product_wrapper:hover .img-hover {
                        opacity: 1;
                      }
                      .card-product_wrapper:hover .product-action_list {
                        opacity: 1;
                        visibility: visible;
                      }
                    `}
                  </style>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* ----------------- Artificial Jewellery Section ----------------- */}
      <section className="flat-spacing p-0 py-5">
        <div className="container">
          <h2 className="fw-bold mb-4 text-center">Artificial Jewellery</h2>
          <Swiper
            modules={[Pagination]}
            spaceBetween={30}
            slidesPerView={4}
            pagination={{ clickable: true }}
            breakpoints={{
              320: { slidesPerView: 2, spaceBetween: 12 },
              576: { slidesPerView: 2, spaceBetween: 20 },
              768: { slidesPerView: 3, spaceBetween: 30 },
              1200: { slidesPerView: 4, spaceBetween: 48 },
            }}
            className="tf-swiper wow fadeInUp"
          >
            {artificialProducts.map((item, index) => (
              <SwiperSlide key={index}>
                <div
                  className="card-product_wrapper position-relative overflow-hidden"
                  style={{ transition: "all 0.3s ease" }}
                >
                  <a href="/product-detail" className="product-img d-block position-relative">
                    <img src={item.img} alt={item.name} className="img-product w-100" />
                    <img
                      src={item.hoverImg}
                      alt={item.name}
                      className="img-hover w-100 position-absolute top-0 start-0"
                      style={{ opacity: 0, transition: "opacity 0.3s ease" }}
                    />
                  </a>

                  {/* Hover icons */}
                  <HoverIcons />

                  <div className="card-product_info text-center mt-3">
                    <a href="/product-detail" className="name-product h5 link d-block">
                      {item.name}
                    </a>
                    <div className="price-wrap mb-0">
                      <span className="price-old h6 fw-normal text-decoration-line-through">
                        {item.oldPrice}
                      </span>{" "}
                      <span className="price-new h6 text-danger fw-bold">{item.newPrice}</span>
                    </div>
                  </div>

                  <style>
                    {`
                      .card-product_wrapper:hover .img-hover {
                        opacity: 1;
                      }
                      .card-product_wrapper:hover .product-action_list {
                        opacity: 1;
                        visibility: visible;
                      }
                      .product-action_list a:hover {
                        transform: scale(1.1);
                        background: #f4c542;
                      }
                    `}
                  </style>
                </div>
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
      </section>
    </div>
  );
};

export default CombinedProductPage;
