import React, { useState } from "react";
import "../App.css";
import "../index.css";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";
import "../css/styles.css";
import "../css/animate.css";
import "../icon/icomoon/style.css";

import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Mousewheel } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";

// Example product images
import mainImg from "../images/products/jewelry/product-13.jpg";
import img2 from "../images/products/jewelry/product-14.jpg";
import img3 from "../images/products/jewelry/product-15.jpg";
import img4 from "../images/products/jewelry/product-16.jpg";

const ProductDetail = () => {
  const images = [mainImg, img2, img3, img4];
  const [selectedImage, setSelectedImage] = useState(mainImg);

  return (
    <>

      <div className="container py-5">
        <div className="row justify-content-center">
          {/* Left Side: Vertical Slider */}
          <div className="col-md-2 col-3">
            <Swiper
              direction="vertical"
              slidesPerView={4}
              spaceBetween={10}
              mousewheel={true}
              navigation={true}
              modules={[Navigation, Mousewheel]}
              className="product-thumb-slider"
              style={{ height: "400px" }}
            >
              {images.map((img, index) => (
                <SwiperSlide key={index}>
                  <img
                    src={img}
                    alt={`Product ${index}`}
                    className={`img-fluid rounded thumbnail-img ${
                      selectedImage === img ? "active" : ""
                    }`}
                    onClick={() => setSelectedImage(img)}
                    style={{
                      cursor: "pointer",
                      border: selectedImage === img ? "2px solid gold" : "1px solid #ddd",
                    }}
                  />
                </SwiperSlide>
              ))}
            </Swiper>
          </div>

          {/* Right Side: Main Image with Zoom */}
          <div className="col-md-6 col-9">
            <div className="main-image-wrapper">
              <img
                src={selectedImage}
                alt="Selected Product"
                className="img-fluid main-image zoom-effect"
              />
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductDetail;
